import React, { useState } from "react";
import ReactDom from "react-dom";
import Hero from "../components/Hero";
import Content from "../components/Content";
import axios from "axios";

function BookSearch(props) {
  const [book, setBook] = useState("");
  const [height, setHeight] = useState("");
  const [result, setResult] = useState([]);
  const [apiKey, setApiKey] = useState(
    "AIzaSyDt5mXFwvWgydKIRaJuWccj2yKB8VtXCms"
  );

  function handleChange(event) {
    const book = event.target.value;
    setBook(book);
  }

  function handleHeight(event) {
    const height = event.target.value;
    setHeight(height);
  }

  async function filterBooks(book) {
    let h = 0;
    let bool = true;
    await axios
      .get(
        "https://www.googleapis.com/books/v1/volumes/" +
          book.id +
          "?key=" +
          apiKey
      )
      .then((data) => {
        if (!data.data.volumeInfo.hasOwnProperty("dimensions")) {
          bool = false;
        } else {
          h = data.data.volumeInfo.dimensions.height;
        }
        if (!data.data.volumeInfo.hasOwnProperty("imageLinks")) {
          bool = false;
        } else if (
          !data.data.volumeInfo.imageLinks.hasOwnProperty("thumbnail")
        ) {
          bool = false;
        }
      });
    console.log(
      book.volumeInfo.title +
        " has a height of " +
        h +
        " compared to limit of height " +
        height +
        bool
    );
    return bool && h <= height;
  }

  async function handleSubmit(event) {
    event.preventDefault();
    let d;
    await axios
      .get(
        "https://www.googleapis.com/books/v1/volumes?q=" +
          book +
          "&orderBy=newest&printType=books&key=" +
          apiKey
      )
      .then((data) => {
        d = data;
        console.log(data.data.items);
        setResult(data.data.items);
      })
      .catch(() => {
        console.log("error");
      });

    //result.forEach(fun(d));
  }

  function fun(book) {
    console.log(book);
    ///console.log(book.volumeInfo.title);
  }

  return (
    <div>
      <Hero title={props.title}></Hero>
      <div class="container">
        <form onSubmit={handleSubmit}>
          <div class="form-group">
            <input
              type="text"
              onChange={handleChange}
              className="form-control mt-10"
              placeholder="search for books"
              autoComplete="off"
            />
            <br className="p-5"></br>
            <input
              type="text"
              onChange={handleHeight}
              className="form-input"
              placeholder="enter max height in cm"
              autoComplete="off"
            />
            <button type="submit" className="btn btn-danger">
              search
            </button>
          </div>
        </form>
        {result.filter(filterBooks).map((book) => (
          <a target="_blank" href={book.volumeInfo.previewLink}>
            <img src={book.volumeInfo.imageLinks.thumbnail} alt={book.title} />
          </a>
        ))}
      </div>
    </div>
  );
}

export default BookSearch;
