import React, { useState } from "react";
import ReactDom from "react-dom";
import Hero from "../components/Hero";
import Content from "../components/Content";
import axios from "axios";

function BookSearch(props) {
  const [book, setBook] = useState("");
  const [height, setHeight] = useState("");
  const [result, setResult] = useState([]);
  const [apiKey, setApiKey] = useState(
    "AIzaSyDt5mXFwvWgydKIRaJuWccj2yKB8VtXCms"
  );
  const [id, setID] = useState([]);

  function getFinalBooks() {}

  function checkDuplicates() {}

  function handleChange(event) {
    const book = event.target.value;
    setBook(book);
  }

  function printResult() {
    console.log(result);
  }

  function handleHeight(event) {
    const height = event.target.value;
    setHeight(height);
  }

  function printHeight(book) {
    let h = 0;
    axios
      .get(
        "https://www.googleapis.com/books/v1/volumes/" +
          book.id +
          "?key=" +
          apiKey
      )
      .then((data) => {
        if (!data.data.volumeInfo.dimensions) {
          return false;
        } else {
          h = data.data.volumeInfo.dimensions.height;
        }
        if (!data.data.volumeInfo.imageLinks) {
          return false;
        }
        console.log(data.data);
        if (
          data.data.saleInfo.saleability.localeCompare("NOT_FOR_SALE") === 0
        ) {
          console.log(data.data.saleInfo.saleability);
          return false;
        }
      });

    return h < height;
  }

  function handleSubmit(event) {
    event.preventDefault();
    axios
      .get(
        "https://www.googleapis.com/books/v1/volumes?q=" +
          book +
          "&orderBy=newest&printType=books&key=" +
          apiKey
      )
      .then((data) => {
        setResult(data.data.items);
      });

    while (result.size == 0) {}
    printResult();
  }

  return (
    <div>
      <Hero title={props.title}></Hero>
      <div class="container">
        <form onSubmit={handleSubmit}>
          <div class="form-group">
            <input
              type="text"
              onChange={handleChange}
              className="form-control mt-10"
              placeholder="search for books"
              autoComplete="off"
            />
            <br className="p-5"></br>
            <input
              type="text"
              onChange={handleHeight}
              className="form-input"
              placeholder="height of bookshelf"
              autoComplete="off"
            />
            <button type="submit" className="btn btn-danger">
              search
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default BookSearch;
